var todoList={todos:[],addTodo:function(o){this.todos.push({todoText:o,completed:!1})},changeTodo:function(o,t){this.todos[o].todoText=t},deleteTodo:function(o){this.todos.splice(o,1)},toggleCompleted:function(o){var t=this.todos[o];t.completed=!t.completed},toggleAll:function(){for(var o=this.todos.length,t=0,e=0;o>e;e++)this.todos[e].completed===!0&&t++;if(t===o)for(var e=0;o>e;e++)this.todos[e].completed=!1;else for(var e=0;o>e;e++)this.todos[e].completed=!0}},handlers={addTodo:function(){var o=document.getElementById("addTodoTextInput");todoList.addTodo(o.value),o.value="",view.displayTodos()},changeTodo:function(){var o=document.getElementById("changeTodoPositionInput"),t=document.getElementById("changeTodoTextInput");todoList.changeTodo(o.valueAsNumber,t.value),o.value="",t.value="",view.displayTodos()},deleteTodo:function(){var o=document.getElementById("deleteTodoPositionInput");todoList.deleteTodo(o.valueAsNumber),o.value="",view.displayTodos()},toggleCompleted:function(){var o=document.getElementById("toggleCompletedPositionInput");todoList.toggleCompleted(o.valueAsNumber),o.value="",view.displayTodos()},toggleAll:function(){todoList.toggleAll(),view.displayTodos()}},view={displayTodos:function(){var o=document.querySelector("ul");o.innerHTML="";for(var t=0;t<todoList.todos.length;t++){var e=document.createElement("li"),d=todoList.todos[t],l="";l=d.completed===!0?"(o) "+d.todoText:"(x) "+d.todoText,e.textContent=l,o.appendChild(e)}}};