var todoList={todos:[],displayTodos:function(){if(0===this.todos.length)console.log("The todo list is empty!");else{console.log("My Todos:");for(var o=0;o<this.todos.length;o++)this.todos[o].completed===!0?console.log("(o)",this.todos[o].todoText):console.log("(x)",this.todos[o].todoText)}},addTodo:function(o){this.todos.push({todoText:o,completed:!1}),this.displayTodos()},changeTodo:function(o,t){this.todos[o].todoText=t,this.displayTodos()},deleteTodo:function(o){this.todos.splice(o,1),this.displayTodos()},toggleCompleted:function(o){var t=this.todos[o];t.completed=!t.completed,this.displayTodos()},toggleAll:function(){for(var o=this.todos.length,t=0,e=0;o>e;e++)this.todos[e].completed===!0&&t++;if(t===o)for(var e=0;o>e;e++)this.todos[e].completed=!1;else for(var e=0;o>e;e++)this.todos[e].completed=!0;this.displayTodos()}},handlers={displayTodos:function(){todoList.displayTodos()},addTodo:function(){var o=document.getElementById("addTodoTextInput");todoList.addTodo(o.value),o.value=""},changeTodo:function(){var o=document.getElementById("changeTodoPositionInput"),t=document.getElementById("changeTodoTextInput");todoList.changeTodo(o.valueAsNumber,t.value),o.value="",t.value=""},deleteTodo:function(){var o=document.getElementById("deleteTodoPositionInput");todoList.deleteTodo(o.valueAsNumber),o.value=""},toggleCompleted:function(){var o=document.getElementById("toggleCompletedPositionInput");todoList.toggleCompleted(o.valueAsNumber),o.value=""},toggleAll:function(){todoList.toggleAll()}},view={displayTodos:function(){var o=document.querySelector("ul");o.innerHTML="";for(var t=0;t<todoList.todos.length;t++){var e=document.createElement("li");e.textContent=todoList.todos[t].todoText,o.appendChild(e)}}};